                    <button className="px-3 py-2 rounded-lg font-medium transition-colors"
                      style={{ backgroundColor: colors.primary, color: 'white' }}>
                      Edit
                    </button>
                    <button className="px-3 py-2 rounded-lg font-medium transition-colors"
                      style={{ backgroundColor: colors.error, color: 'white' }}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const RequestsTab = () => (
    <div>
      <h2 className="text-3xl font-bold mb-8" style={{ color: colors.primary }}>{t.requests}</h2>

      <div className="grid gap-6">
        {emergencyRequests.map((request) => (
          <div key={request.id} className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-2xl shadow-xl p-8`}>
            <div className="flex items-start justify-between mb-4">
              <div>
                <h3 className="text-xl font-bold">{language === 'hi' ? request.name : (request.nameEn || request.name)}</h3>
                <p className="opacity-75">{request.phone}</p>
                <p className="text-sm opacity-50">{new Date(request.timestamp).toLocaleString()}</p>
              </div>
              <StatusBadge status={request.status} />
            </div>
            <div className="mb-4">
              <span className="px-3 py-1 rounded-full text-sm font-medium"
                style={{ backgroundColor: colors.accent + '30', color: colors.text }}>
                {language === 'hi' ?
                  (request.type === 'medical' ? 'चिकित्सा आपातकाल' :
                    request.type === 'shelter' ? 'आश्रय की जरूरत' :
                      request.type === 'food' ? 'भोजन की जरूरत' :
                        request.type === 'legal' ? 'कानूनी सहायता' :
                          request.type === 'accident' ? 'दुर्घटना' :
                            request.type === 'harassment' ? 'उत्पीड़न/धमकी' :
                              request.type) :
                  request.type}
              </span>
            </div>
            <p className="mb-4">{language === 'hi' ? (request.descriptionHi || request.description) : request.description}</p>
            <div className="flex gap-3">
              <button className="text-white px-4 py-2 rounded-lg font-medium"
                style={{ backgroundColor: colors.success }}>
                Resolve
              </button>
              <button className="text-white px-4 py-2 rounded-lg font-medium"
                style={{ backgroundColor: colors.primary }}>
                Contact
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const MapTab = () => (
    <div>
      <h2 className="text-3xl font-bold mb-8" style={{ color: colors.primary }}>{t.mapView}</h2>

      <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-2xl shadow-xl p-8 text-center`}>
        <Navigation size={120} className="mx-auto mb-6" style={{ color: colors.primary }} />
        <h3 className="text-2xl font-bold mb-4">Migration Trends Map</h3>
        <p className="opacity-75 mb-6">
          {language === 'hi'
            ? 'यहाँ प्रवासियों के आंदोलन का नक्शा दिखाया जाएगा। बिहार से भारत के विभिन्न शहरों में जाने वाले मजदूरों का डेटा।'
            : 'Interactive map showing migration patterns from Bihar to various cities across India will be displayed here.'}
        </p>
        <button className="text-white px-6 py-3 rounded-xl font-medium"
          style={{ backgroundColor: colors.primary }}>
          {language === 'hi' ? 'मैप लोड करें' : 'Load Map'}
        </button>
      </div>
    </div>
  );

  const ReportsTab = () => (
    <div>
      <h2 className="text-3xl font-bold mb-8" style={{ color: colors.primary }}>{t.reports}</h2>

      <div className="grid md:grid-cols-2 gap-8 mb-8">
        <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-2xl shadow-xl p-8`}>
          <BarChart3 size={60} className="mb-4" style={{ color: colors.primary }} />
          <h3 className="text-xl font-bold mb-4">Monthly Registration Trends</h3>
          <div className="space-y-3">
            <div className="flex justify-between">
              <span>January 2024</span>
              <span className="font-bold">45</span>
            </div>
            <div className="flex justify-between">
              <span>December 2023</span>
              <span className="font-bold">38</span>
            </div>
            <div className="flex justify-between">
              <span>November 2023</span>
              <span className="font-bold">52</span>
            </div>
          </div>
        </div>

        <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-2xl shadow-xl p-8`}>
          <FileText size={60} className="mb-4" style={{ color: colors.accent }} />
          <h3 className="text-xl font-bold mb-4">Available Reports</h3>
          <div className="space-y-3">
            <ReportItem title="Migration Summary" date="2024-01-22" />
            <ReportItem title="Emergency Analytics" date="2024-01-20" />
            <ReportItem title="Job Placement Data" date="2024-01-18" />
          </div>
        </div>
      </div>

      <div className="flex gap-4">
        <button className="text-white px-6 py-3 rounded-xl font-medium"
          style={{ backgroundColor: colors.primary }}>
          <Download size={20} className="inline mr-2" />
          {language === 'hi' ? 'पूरी रिपोर्ट डाउनलोड करें' : 'Download Full Report'}
        </button>
        <button className="text-white px-6 py-3 rounded-xl font-medium"
          style={{ backgroundColor: colors.accent, color: colors.text }}>
          <BarChart3 size={20} className="inline mr-2" />
          {language === 'hi' ? 'एनालिटिक्स देखें' : 'View Analytics'}
        </button>
      </div>
    </div>
  );

  const ReportItem = ({ title, date }) => (
    <div className="flex items-center justify-between p-3 rounded-lg bg-gray-50 hover:bg-gray-100 transition-colors">
      <div>
        <div className="font-medium">{title}</div>
        <div className="text-sm opacity-75">{date}</div>
      </div>
      <button className="text-white px-3 py-1 rounded-lg text-sm font-medium"
        style={{ backgroundColor: colors.primary }}>
        <Download size={16} />
      </button>
    </div>
  );

  const AdminStatsCard = ({ icon: Icon, number, label, color }) => (
    <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-2xl shadow-xl p-6`}>
      <div className="flex items-center">
        <div className="p-3 rounded-xl mr-4" style={{ backgroundColor: color + '20' }}>
          <Icon size={28} style={{ color }} />
        </div>
        <div>
          <p className="text-3xl font-bold" style={{ color }}>{number}</p>
          <p className="text-sm opacity-75">{label}</p>
        </div>
      </div>
    </div>
  );

  const StatusBadge = ({ status }) => {
    const getStatusColor = (status) => {
      switch (status) {
        case 'active': return colors.success;
        case 'pending': return colors.warning;
        case 'resolved': return colors.primary;
        default: return colors.text;
      }
    };

    const getStatusIcon = (status) => {
      switch (status) {
        case 'active': return CheckCircle;
        case 'pending': return Clock;
        case 'resolved': return CheckCircle;
        default: return AlertCircle;
      }
    };

    const StatusIcon = getStatusIcon(status);

    return (
      <span
        className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium text-white"
        style={{ backgroundColor: getStatusColor(status) }}
      >
        <StatusIcon size={14} className="mr-1" />
        {status}
      </span>
    );
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'home': return <HomePage />;
      case 'register': return <RegistrationPage />;
      case 'support': return <SupportPage />;
      case 'jobs': return <JobBoardPage />;
      case 'emergency': return <EmergencyPage />;
      case 'admin': return <AdminDashboard />;
      default: return <HomePage />;
    }
  };

  // Chatbot Widget
  const ChatbotWidget = () => (
    <div className="fixed bottom-6 left-6 z-50">
      {chatOpen && (
        <div className={`${darkMode ? 'bg-gray-800' : 'bg-white'} rounded-2xl shadow-2xl mb-4`} style={{ width: '350px', maxHeight: '500px' }}>
          <div className="p-4 border-b" style={{ borderColor: colors.primary }}>
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 rounded-full" style={{ backgroundColor: colors.success }}></div>
                <h3 className="font-bold" style={{ color: colors.primary }}>
                  {language === 'hi' ? 'सहायक चैटबॉट' : 'Helper Chatbot'}
                </h3>
              </div>
              <button
                onClick={() => setChatOpen(false)}
                className="p-1 rounded-lg hover:bg-gray-100 transition-colors"
              >
                <X size={18} />
              </button>
            </div>
          </div>

          <div className="h-80 overflow-y-auto p-4 space-y-3">
            {messages.length === 0 && (
              <div className="text-center opacity-75 py-8">
                <Heart size={40} className="mx-auto mb-2" style={{ color: colors.primary }} />
                <p className="text-sm">
                  {language === 'hi'
                    ? 'नमस्ते! मैं आपकी सहायता के लिए यहाँ हूँ।'
                    : 'Hello! I am here to help you.'}
                </p>
              </div>
            )}

            {messages.map((msg, index) => (
              <div
                key={index}
                className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div
                  className={`max-w-xs px-4 py-2 rounded-2xl text-sm ${msg.sender === 'user'
                      ? 'text-white'
                      : darkMode ? 'bg-gray-700' : 'bg-gray-100'
                    }`}
                  style={msg.sender === 'user' ? { backgroundColor: colors.primary } : {}}
                >
                  {msg.text}
                </div>
              </div>
            ))}

            {chatLoading && (
              <div className="flex justify-start">
                <div className={`max-w-xs px-4 py-2 rounded-2xl text-sm ${darkMode ? 'bg-gray-700' : 'bg-gray-100'}`}>
                  <div className="flex space-x-1">
                    <div className="w-2 h-2 rounded-full bg-gray-400 animate-bounce"></div>
                    <div className="w-2 h-2 rounded-full bg-gray-400 animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                    <div className="w-2 h-2 rounded-full bg-gray-400 animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                  </div>
                </div>
              </div>
            )}
          </div>

          <div className="p-4 border-t" style={{ borderColor: colors.primary + '30' }}>
            <div className="flex space-x-2">
              <input
                type="text"
                value={chatInput}
                onChange={(e) => setChatInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                placeholder={language === 'hi' ? 'अपना संदेश लिखें...' : 'Type your message...'}
                className={`flex-1 p-3 rounded-xl border-2 text-sm ${darkMode ? 'bg-gray-700 border-gray-600' : 'bg-white border-gray-300'
                  }`}
                disabled={chatLoading}
              />
              <button
                onClick={sendMessage}
                disabled={chatLoading || !chatInput.trim()}
                className="px-4 py-3 rounded-xl text-white font-medium transition-all disabled:opacity-50"
                style={{ backgroundColor: colors.primary }}
              >
                <Phone size={16} />
              </button>
            </div>
          </div>
        </div>
      )}

      <button
        onClick={() => setChatOpen(!chatOpen)}
        className="w-16 h-16 rounded-full text-white shadow-2xl transition-all hover:scale-110 flex items-center justify-center"
        style={{ backgroundColor: colors.primary }}
      >
        {chatOpen ? <X size={24} /> : <Phone size={24} />}
      </button>
    </div>
  );

  const FooterLink = ({ onClick, text }) => (
    <button
      onClick={onClick}
      className="block w-full text-left hover:opacity-75 transition-opacity font-medium"
    >
      {text}
    </button>
  );

  return (
    <div
      className="transition-colors duration-300"
      style={{
        fontSize: `${fontSize}px`,
        backgroundColor: darkMode ? colors.backgroundDark : colors.background,
        color: darkMode ? colors.textDark : colors.text
      }}
    >
      <Navbar />
      {renderCurrentPage()}
      <AccessibilityToolbar />
      <ChatbotWidget />

      {/* Footer */}
      <footer className={`${darkMode ? 'bg-gray-800' : 'bg-white'} border-t-2 mt-16`} style={{ borderColor: colors.primary }}>
        <div className="max-w-7xl mx-auto px-4 py-12">
          <div className="grid md:grid-cols-4 gap-8">
            <div className="md:col-span-2">
              <div className="flex items-center mb-6">
                <Heart className="h-10 w-10 mr-3" style={{ color: colors.primary }} />
                <div>
                  <div className="text-2xl font-bold" style={{ color: colors.primary }}>
                    Migration Connect
                  </div>
                  <div className="opacity-75">{t.tagline}</div>
                </div>
              </div>
              <p className="text-lg opacity-75 mb-6 max-w-md">
                {language === 'hi'
                  ? 'बिहार के प्रवासी मजदूरों के लिए एक विश्वसनीय सहायता मंच। हमारा उद्देश्य हर मजदूर को उनके अधिकार और सुविधाएं दिलाना है।'
                  : 'A trusted support platform for migrant workers from Bihar. Our mission is to ensure every worker gets their rights and facilities.'}
              </p>
              <div className="flex space-x-4">
                <button className="p-3 rounded-xl transition-colors" style={{ backgroundColor: colors.primary + '20' }}>
                  <Phone size={20} style={{ color: colors.primary }} />
                </button>
                <button className="p-3 rounded-xl transition-colors" style={{ backgroundColor: colors.primary + '20' }}>
                  <Mail size={20} style={{ color: colors.primary }} />
                </button>
                <button className="p-3 rounded-xl transition-colors" style={{ backgroundColor: colors.primary + '20' }}>
                  <Globe size={20} style={{ color: colors.primary }} />
                </button>
              </div>
            </div>

            <div>
              <h4 className="font-bold text-xl mb-6" style={{ color: colors.primary }}>
                {language === 'hi' ? '📞 संपर्क करें' : '📞 Contact Us'}
              </h4>
              <div className="space-y-4">
                <div className="flex items-center">
                  <Phone size={16} className="mr-3 opacity-75" />
                  <div>
                    <div className="font-medium">1800-891-1291</div>
                    <div className="text-sm opacity-75">
                      {language === 'hi' ? '24x7 हेल्पलाइन' : '24x7 Helpline'}
                    </div>
                  </div>
                </div>
                <div className="flex items-center">
                  <Mail size={16} className="mr-3 opacity-75" />
                  <div>
                    <div className="font-medium">help@migrationconnect.org</div>
                    <div className="text-sm opacity-75">
                      {language === 'hi' ? 'ईमेल सहायता' : 'Email Support'}
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div>
              <h4 className="font-bold text-xl mb-6" style={{ color: colors.primary }}>
                {language === 'hi' ? '🔗 त्वरित लिंक' : '🔗 Quick Links'}
              </h4>
              <div className="space-y-3">
                <FooterLink onClick={() => setCurrentPage('support')} text={t.findSupport} />
                <FooterLink onClick={() => setCurrentPage('jobs')} text={t.jobBoard} />
                <FooterLink onClick={() => setCurrentPage('emergency')} text={t.emergency} />
                <FooterLink onClick={() => setCurrentPage('register')} text={t.register} />
              </div>
            </div>
          </div>

          <div className="border-t-2 mt-12 pt-8 text-center" style={{ borderColor: colors.primary + '30' }}>
            <p className="text-lg font-medium mb-2">
              {language === 'hi'
                ? '🙏 आपका साथ, हमारी शक्ति'
                : '🙏 Your Support, Our Strength'}
            </p>
            <p className="opacity-75">
              &copy; 2024 Migration Connect.
              {language === 'hi'
                ? ' सभी अधिकार सुरक्षित। बिहार सरकार के सहयोग से।'
                : ' All rights reserved. In collaboration with Government of Bihar.'}
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default MigrationConnect;
