import React, { useState, useEffect } from 'react';
import {
  Users,
  AlertTriangle,
  Briefcase,
  Route,
  Phone,
  MapPin,
  Clock,
  DollarSign,
  Home,
  Utensils,
  PhoneCall,
  CheckCircle,
  Flag,
  Star,
  Mic,
  MicOff,
  Plus,
  Minus,
  Moon,
  Sun,
  Download,
  Search,
  Filter,
  Grid,
  List,
  Settings,
  Bell,
  UserPlus,
  HandHeart,
  Navigation,
  Eye,
  Shield,
  RefreshCw,
  Menu,
  X
} from 'lucide-react';

const MigrantSupportPlatform = () => {
  // Global state
  const [currentPage, setCurrentPage] = useState('home');
  const [currentLanguage, setCurrentLanguage] = useState('en');
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isAccessibilityOpen, setIsAccessibilityOpen] = useState(false);
  const [fontSize, setFontSize] = useState(16);
  const [isHighContrast, setIsHighContrast] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Registration form state
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    fullName: '',
    phone: '',
    aadhaar: '',
    originState: '',
    originDistrict: '',
    currentCity: '',
    workType: '',
    experience: '',
    emergencyContact: '',
    emergencyPhone: ''
  });

  // Emergency support state
  const [selectedCity, setSelectedCity] = useState('');
  const [activeTab, setActiveTab] = useState('food');

  // Job board state
  const [jobView, setJobView] = useState('grid');
  const [jobFilters, setJobFilters] = useState({
    location: '',
    wage: '',
    type: ''
  });

  // Notification state
  const [notifications, setNotifications] = useState([]);

  // Sample data
  const languages = [
    { code: 'en', label: 'EN', name: 'English' },
    { code: 'hi', label: 'हिं', name: 'Hindi' },
    { code: 'mai', label: 'मै', name: 'Maithili' },
    { code: 'bho', label: 'भो', name: 'Bhojpuri' }
  ];

  const emergencyServices = {
    food: [
      {
        id: 1,
        name: 'Gurudwara Bangla Sahib',
        address: 'Ashoka Road, Connaught Place, New Delhi',
        description: 'Free meals available 24/7. No documentation required.',
        phone: '+91-11-2336-2358'
      },
      {
        id: 2,
        name: 'Akshaya Patra Kitchen',
        address: 'Sector 12, Dwarka, New Delhi',
        description: 'Hot meals served from 12 PM to 8 PM daily.',
        phone: '+91-11-4567-8901'
      }
    ],
    shelter: [
      {
        id: 1,
        name: 'Rain Basera Shelter',
        address: 'ISBT Kashmere Gate, Delhi',
        description: 'Night shelter with capacity for 200 people. Registration required.',
        phone: '+91-11-2345-6789'
      },
      {
        id: 2,
        name: 'Yamuna Pushta Shelter',
        address: 'Near Red Fort, Delhi',
        description: '24-hour shelter with basic amenities and medical support.',
        phone: '+91-11-3456-7890'
      }
    ],
    helplines: [
      {
        id: 1,
