                {label}
              </button>
            ))}
          </div>
        </div>

        <div className="mb-8">
          <div className="bg-gray-300 rounded-lg h-64 flex items-center justify-center">
            <div className="text-center text-gray-600">
              <MapPin className="h-12 w-12 mx-auto mb-2" />
              <p>Interactive Map would appear here</p>
              <p className="text-sm">Showing nearby {activeTab} services</p>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {emergencyServices[activeTab]?.map(service => (
            <div key={service.id} className="bg-white p-6 rounded-lg shadow-md border-l-4 border-blue-600">
              <h3 className="text-lg font-semibold text-gray-900 mb-2 flex items-center">
                {activeTab === 'food' && <Utensils className="h-5 w-5 mr-2 text-blue-600" />}
                {activeTab === 'shelter' && <Home className="h-5 w-5 mr-2 text-blue-600" />}
                {activeTab === 'helplines' && <PhoneCall className="h-5 w-5 mr-2 text-blue-600" />}
                {service.name}
              </h3>
              <p className="text-gray-600 text-sm mb-2">{service.address}</p>
              <p className="text-gray-700 mb-4">{service.description}</p>
              <div className="flex gap-2">
                <button className="bg-blue-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-700 flex items-center">
                  <Phone className="h-4 w-4 mr-1" />
                  Call
                </button>
                <button className="bg-gray-200 text-gray-700 px-4 py-2 rounded-md text-sm font-medium hover:bg-gray-300 flex items-center">
                  <Navigation className="h-4 w-4 mr-1" />
                  Directions
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // Job Board Page Component
  const JobBoardPage = () => (
    <div className="pt-20 pb-12 bg-gray-50 min-h-screen">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8 gap-4">
          <h2 className="text-3xl font-bold text-gray-900">Job Opportunities</h2>

          <div className="flex flex-wrap gap-4 items-center">
            <div className="flex items-center gap-2">
              <label className="text-sm font-medium text-gray-700">Location:</label>
              <select
                value={jobFilters.location}
                onChange={(e) => setJobFilters(prev => ({ ...prev, location: e.target.value }))}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm"
              >
                <option value="">All Cities</option>
                <option value="delhi">Delhi</option>
                <option value="mumbai">Mumbai</option>
                <option value="bangalore">Bangalore</option>
              </select>
            </div>

            <div className="flex items-center gap-2">
              <label className="text-sm font-medium text-gray-700">Wage:</label>
              <select
                value={jobFilters.wage}
                onChange={(e) => setJobFilters(prev => ({ ...prev, wage: e.target.value }))}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm"
              >
                <option value="">All Wages</option>
                <option value="0-15000">₹0 - ₹15,000</option>
                <option value="15000-25000">₹15,000 - ₹25,000</option>
                <option value="25000+">₹25,000+</option>
              </select>
            </div>

            <div className="flex border border-gray-300 rounded-md overflow-hidden">
              <button
                onClick={() => setJobView('grid')}
                className={`p-2 ${jobView === 'grid' ? 'bg-blue-600 text-white' : 'bg-white text-gray-600'}`}
              >
                <Grid className="h-4 w-4" />
              </button>
              <button
                onClick={() => setJobView('list')}
                className={`p-2 ${jobView === 'list' ? 'bg-blue-600 text-white' : 'bg-white text-gray-600'}`}
              >
                <List className="h-4 w-4" />
              </button>
            </div>
          </div>
        </div>

        <div className={`grid gap-6 ${jobView === 'grid' ? 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3' : 'grid-cols-1'}`}>
          {jobListings.map(job => (
            <div key={job.id} className="bg-white p-6 rounded-lg shadow-md relative">
              {job.isScam && (
                <div className="bg-red-50 border border-red-200 text-red-700 p-3 rounded-md mb-4 flex items-center text-sm">
                  <AlertTriangle className="h-4 w-4 mr-2" />
                  Potential scam detected - Proceed with caution
                </div>
              )}

              <div className="absolute top-4 right-4">
                <span className={`px-2 py-1 text-xs font-medium rounded-full ${job.status === 'verified' ? 'bg-green-100 text-green-800' :
                    job.status === 'flagged' ? 'bg-red-100 text-red-800' :
                      'bg-orange-100 text-orange-800'
                  }`}>
                  {job.status === 'verified' && <CheckCircle className="h-3 w-3 inline mr-1" />}
                  {job.status === 'flagged' && <Flag className="h-3 w-3 inline mr-1" />}
                  {job.status === 'new' && <Star className="h-3 w-3 inline mr-1" />}
                  {job.status.charAt(0).toUpperCase() + job.status.slice(1)}
                </span>
              </div>

              <h3 className="text-lg font-semibold text-gray-900 mb-2 pr-20">{job.title}</h3>

              <div className="flex flex-wrap gap-4 mb-3 text-sm text-gray-600">
                <div className="flex items-center">
                  <MapPin className="h-4 w-4 mr-1" />
                  {job.location}
                </div>
                <div className="flex items-center">
                  <DollarSign className="h-4 w-4 mr-1" />
                  {job.wage}
                </div>
                <div className="flex items-center">
                  <Clock className="h-4 w-4 mr-1" />
                  {job.type}
                </div>
              </div>

              <p className="text-gray-700 mb-4 text-sm">{job.description}</p>

              <div className="flex gap-2">
                {!job.isScam ? (
                  <>
                    <button
                      onClick={() => showNotification(`Calling ${job.company}...`, 'info')}
                      className="bg-blue-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-700 flex items-center"
                    >
                      <Phone className="h-4 w-4 mr-1" />
                      Call
                    </button>
                    <button
                      onClick={() => showNotification('Application submitted successfully!', 'success')}
                      className="bg-gray-200 text-gray-700 px-4 py-2 rounded-md text-sm font-medium hover:bg-gray-300 flex items-center"
                    >
                      <UserPlus className="h-4 w-4 mr-1" />
                      Apply
                    </button>
                  </>
                ) : (
                  <button disabled className="bg-gray-100 text-gray-400 px-4 py-2 rounded-md text-sm font-medium cursor-not-allowed flex items-center">
                    <Flag className="h-4 w-4 mr-1" />
                    Reported
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  // Admin Dashboard Page Component
  const AdminDashboardPage = () => (
    <div className="pt-20 pb-12 bg-gray-50 min-h-screen">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center mb-8">
          <h2 className="text-3xl font-bold text-gray-900">Admin Dashboard</h2>

          <div className="flex items-center gap-2">
            <span className="text-sm font-medium text-gray-700">Dark Mode</span>
            <button
              onClick={() => setIsDarkMode(!isDarkMode)}
              className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${isDarkMode ? 'bg-blue-600' : 'bg-gray-200'
                }`}
            >
              <span
                className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${isDarkMode ? 'translate-x-6' : 'translate-x-1'
                  }`}
              />
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {adminStats.map((stat, index) => {
            const Icon = stat.icon;
            return (
              <div key={index} className="bg-white p-6 rounded-lg shadow-md border-l-4 border-blue-600">
                <div className="flex items-center justify-between">
                  <div>
                    <div className="flex items-center mb-2">
                      <div className="bg-blue-100 p-2 rounded-lg">
                        <Icon className="h-6 w-6 text-blue-600" />
                      </div>
                    </div>
                    <p className="text-2xl font-bold text-gray-900 mb-1">{stat.value}</p>
                    <p className="text-sm text-gray-600 mb-2">{stat.label}</p>
                    <p className={`text-xs ${stat.color === 'red' ? 'text-red-600' :
                        stat.color === 'green' ? 'text-green-600' :
                          stat.color === 'yellow' ? 'text-yellow-600' :
                            'text-blue-600'
                      }`}>
                      {stat.change}
                    </p>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex justify-between items-center mb-6 pb-4 border-b">
              <h3 className="text-lg font-semibold text-gray-900">Registration Analytics</h3>
              <select className="px-3 py-1 border border-gray-300 rounded-md text-sm">
                <option>Last 30 days</option>
                <option>Last 3 months</option>
                <option>Last year</option>
              </select>
            </div>
            <div className="h-64 bg-gray-100 rounded-lg flex items-center justify-center text-gray-500">
              <div className="text-center">
                <Briefcase className="h-12 w-12 mx-auto mb-2" />
                <p>Registration trends chart would appear here</p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex justify-between items-center mb-6 pb-4 border-b">
              <h3 className="text-lg font-semibold text-gray-900">Emergency Requests Feed</h3>
              <button
                onClick={() => showNotification('Emergency feed refreshed', 'info')}
                className="bg-blue-600 text-white px-3 py-1 rounded-md text-sm font-medium hover:bg-blue-700 flex items-center"
              >
                <RefreshCw className="h-4 w-4 mr-1" />
                Refresh
              </button>
            </div>
            <div className="space-y-4 max-h-64 overflow-y-auto">
              {emergencyFeed.map(item => (
                <div key={item.id} className="bg-red-50 border-l-3 border-red-500 p-4 rounded">
                  <div className="flex justify-between items-start mb-1">
                    <strong className="text-red-800">{item.type}</strong>
                    <span className="text-xs text-gray-500">{item.time}</span>
                  </div>
                  <p className="text-sm text-gray-600 mb-1">{item.location}</p>
                  <p className="text-sm text-gray-700">{item.description}</p>
                </div>
              ))}
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex justify-between items-center mb-6 pb-4 border-b">
              <h3 className="text-lg font-semibold text-gray-900">Migration Routes Map</h3>
              <select className="px-3 py-1 border border-gray-300 rounded-md text-sm">
                <option>All States</option>
                <option>Bihar</option>
                <option>UP</option>
                <option>Jharkhand</option>
              </select>
            </div>
            <div className="h-64 bg-gray-100 rounded-lg flex items-center justify-center text-gray-500">
              <div className="text-center">
                <Route className="h-12 w-12 mx-auto mb-2" />
                <p>Interactive migration routes map would appear here</p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow-md">
            <div className="flex justify-between items-center mb-6 pb-4 border-b">
              <h3 className="text-lg font-semibold text-gray-900">Quick Actions</h3>
            </div>
            <div className="grid grid-cols-2 gap-3">
              <button
                onClick={() => showNotification('Data export initiated', 'success')}
                className="bg-blue-600 text-white p-3 rounded-md text-sm font-medium hover:bg-blue-700 flex items-center justify-center"
              >
                <Download className="h-4 w-4 mr-2" />
                Export Data
              </button>
              <button className="bg-gray-200 text-gray-700 p-3 rounded-md text-sm font-medium hover:bg-gray-300 flex items-center justify-center">
                <Users className="h-4 w-4 mr-2" />
                Manage Users
              </button>
              <button className="bg-gray-200 text-gray-700 p-3 rounded-md text-sm font-medium hover:bg-gray-300 flex items-center justify-center">
                <Flag className="h-4 w-4 mr-2" />
                Review Flagged Jobs
              </button>
              <button className="bg-gray-200 text-gray-700 p-3 rounded-md text-sm font-medium hover:bg-gray-300 flex items-center justify-center">
                <Bell className="h-4 w-4 mr-2" />
                Send Alerts
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  // Main App Component
  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'home':
        return <HomePage />;
      case 'registration':
        return <RegistrationPage />;
      case 'emergency':
        return <EmergencyPage />;
      case 'jobs':
        return <JobBoardPage />;
      case 'admin':
        return <AdminDashboardPage />;
      default:
        return <HomePage />;
    }
  };

  useEffect(() => {
    // Initialize welcome notification
    setTimeout(() => {
      showNotification('Welcome to Migrant Support Platform! How can we help you today?', 'success');
    }, 1000);
  }, []);

  return (
    <div className={`min-h-screen ${isDarkMode ? 'dark bg-gray-900 text-white' : 'bg-gray-50'}`}
      style={{ fontSize: `${fontSize}px` }}>
      <NavigationBar />
      <AccessibilityPanel />
      <NotificationContainer />
      {renderCurrentPage()}
    </div>
  );
};

export default MigrantSupportPlatform;
